import React, { useState } from "react";
import galleryData from "./galleryData"; // Ensure this file exports an array of items
import "./Gallery.css";

const Gallery = () => {
  const [category, setCategory] = useState("all");

  const filteredItems = category === "all"
    ? galleryData
    : galleryData.filter((item) => item.category === category);

  console.log("Filtered Items:", filteredItems); // Debugging output

  return (
    <div className="gallery">
      <div className="gallery-categories">
        <button onClick={() => setCategory("all")}>All</button>
        <button onClick={() => setCategory("venue")}>Venues</button>
        <button onClick={() => setCategory("decor")}>Decor</button>
        <button onClick={() => setCategory("food")}>Food</button>
        {/* Add more categories as needed */}
      </div>
      <div className="gallery-items">
        {filteredItems && filteredItems.length > 0 ? (
          filteredItems.slice(0, 5).map((item, index) => (
            <div key={index} className="gallery-item">
              <img src={item.src} alt={item.text} loading="lazy" />
              <p>{item.text}</p>
            </div>
          ))
        ) : (
          <p>No items available for this category.</p>
        )}
      </div>
    </div>
  );
};

export default Gallery;
