import React, { useState } from 'react';
import './ContactUs.css';

const WeddingDetailsForm = () => {
  const [weddingDetails, setWeddingDetails] = useState({
    weddingDate: '',
    weddingCity: '',
    brideName: '',
    groomName: '',
    numberOfGuests: '',
    eventDuration: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setWeddingDetails({
      ...weddingDetails,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission here (you can save the data or move to next step)
    console.log(weddingDetails);
  };

  return (
    <div className="form-container">
      <h2>Wedding Details</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="weddingDate">Wedding Date:</label>
          <input
            type="date"
            id="weddingDate"
            name="weddingDate"
            value={weddingDetails.weddingDate}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="weddingCity">Wedding City:</label>
          <input
            type="text"
            id="weddingCity"
            name="weddingCity"
            value={weddingDetails.weddingCity}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="brideName">Bride's Name:</label>
          <input
            type="text"
            id="brideName"
            name="brideName"
            value={weddingDetails.brideName}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="groomName">Groom's Name:</label>
          <input
            type="text"
            id="groomName"
            name="groomName"
            value={weddingDetails.groomName}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="numberOfGuests">Number of Guests:</label>
          <input
            type="number"
            id="numberOfGuests"
            name="numberOfGuests"
            value={weddingDetails.numberOfGuests}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="eventDuration">Event Duration (in days):</label>
          <input
            type="number"
            id="eventDuration"
            name="eventDuration"
            value={weddingDetails.eventDuration}
            onChange={handleChange}
            required
          />
        </div>

        <button type="submit">Next Step</button>
      </form>
    </div>
  );
};

export default WeddingDetailsForm;
