import React, { useState } from 'react';
import './ContactUs.css';

// Step 1: Customer Information Form
const CustomerInfoForm = ({ formData, setFormData, step, setStep }) => {
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleNext = () => {
    setStep(step + 1); // Move to the next step
  };

  return (
    <div className="form-container">
      <h2>We are delighted you are here! Tell us about yourself</h2>
      <form>
        <div className="form-group">
          <label htmlFor="fullName">Full Name:</label>
          <input
            type="text"
            id="fullName"
            name="fullName"
            value={formData.fullName}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="phoneNumber">Phone Number:</label>
          <input
            type="text"
            id="phoneNumber"
            name="phoneNumber"
            value={formData.phoneNumber}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="email">Email Address:</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="address">Address:</label>
          <input
            type="text"
            id="address"
            name="address"
            value={formData.address}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-navigation">
          <button type="button" onClick={handleNext} className="next-btn">
            Next Step
          </button>
        </div>
      </form>
    </div>
  );
};

// Step 2: Wedding Information Form
const WeddingInfoForm = ({ formData, setFormData, step, setStep }) => {
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleNext = () => {
    setStep(step + 1); // Move to the next step
  };

  const handleBack = () => {
    setStep(step - 1); // Go back to the previous step
  };

  return (
    <div className="form-container">
      <h2>Wedding Information</h2>
      <form>
        <div className="form-group">
          <label htmlFor="weddingDate">Wedding Date:</label>
          <input
            type="date"
            id="weddingDate"
            name="weddingDate"
            value={formData.weddingDate}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="city">Wedding City:</label>
          <input
            type="text"
            id="city"
            name="city"
            value={formData.city}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="brideName">Bride's Name:</label>
          <input
            type="text"
            id="brideName"
            name="brideName"
            value={formData.brideName}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="groomName">Groom's Name:</label>
          <input
            type="text"
            id="groomName"
            name="groomName"
            value={formData.groomName}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="numberOfGuests">Number of Guests:</label>
          <input
            type="number"
            id="numberOfGuests"
            name="numberOfGuests"
            value={formData.numberOfGuests}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="eventDuration">Event Duration (in days):</label>
          <input
            type="number"
            id="eventDuration"
            name="eventDuration"
            value={formData.eventDuration}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-navigation">
          <button type="button" onClick={handleBack} className="prev-btn">
            Previous
          </button>
          <button type="button" onClick={handleNext} className="next-btn">
            Next Step
          </button>
        </div>
      </form>
    </div>
  );
};

// Step 3: Wedding Type Preferences Form
const WeddingTypeForm = ({ formData, setFormData, step, setStep }) => {
    const handleWeddingTypeChange = (e) => {
      const { name, value } = e.target;
      setFormData({
        ...formData,
        [name]: value,
      });
    };
  
    const handleNext = () => {
      setStep(step + 1); // Move to the next step
    };
  
    const handleBack = () => {
      setStep(step - 1); // Go back to the previous step
    };
  
    return (
      <div className="form-container">
        <h2>Wedding Type Preferences</h2>
        <p>Please tell us about the type of wedding you envision.</p>
  
        <form>
          <div className="form-group">
            <label>
              <input
                type="radio"
                name="weddingType"
                value="local"
                checked={formData.weddingType === 'local'}
                onChange={handleWeddingTypeChange}
              />
              Local Wedding
            </label>
          </div>
  
          <div className="form-group">
            <label>
              <input
                type="radio"
                name="weddingType"
                value="national"
                checked={formData.weddingType === 'national'}
                onChange={handleWeddingTypeChange}
              />
              National Destination Wedding
            </label>
          </div>
  
          <div className="form-group">
            <label>
              <input
                type="radio"
                name="weddingType"
                value="international"
                checked={formData.weddingType === 'international'}
                onChange={handleWeddingTypeChange}
              />
              International Destination Wedding
            </label>
          </div>
  
          {/* Show additional fields for Destination Wedding (National or International) */}
          {(formData.weddingType === 'national' || formData.weddingType === 'international') && (
            <div className="form-group">
              <label htmlFor="destination">Preferred Destination:</label>
              <input
                type="text"
                id="destination"
                name="destination"
                value={formData.destination}
                onChange={(e) =>
                  setFormData({ ...formData, destination: e.target.value })
                }
              />
            </div>
          )}
  
          <div className="form-navigation">
            <button type="button" onClick={handleBack} className="prev-btn">
              Previous
            </button>
            <button type="button" onClick={handleNext} className="next-btn">
              Next Step
            </button>
          </div>
        </form>
      </div>
    );
  };
  

const MultiStepForm = () => {
  const [formData, setFormData] = useState({
    fullName: '',
    phoneNumber: '',
    email: '',
    address: '',
    weddingDate: '',
    city: '',
    brideName: '',
    groomName: '',
    numberOfGuests: '',
    eventDuration: '',
    weddingType: '',
    destination: '',
  });
  const [step, setStep] = useState(1);

  return (
    <div className="form-wrapper">
      {step === 1 && (
        <CustomerInfoForm
          formData={formData}
          setFormData={setFormData}
          step={step}
          setStep={setStep}
        />
      )}
      {step === 2 && (
        <WeddingInfoForm
          formData={formData}
          setFormData={setFormData}
          step={step}
          setStep={setStep}
        />
      )}
      {step === 3 && (
        <WeddingTypeForm
          formData={formData}
          setFormData={setFormData}
          step={step}
          setStep={setStep}
        />
      )}
    </div>
  );
};

export default MultiStepForm;
