import React, { useState } from 'react';
import './ContactUs.css';

const WeddingTypeForm = ({ formData, setFormData, step, setStep }) => {
  const handleWeddingTypeChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleNext = () => {
    setStep(step + 1); // Move to the next step
  };

  const handleBack = () => {
    setStep(step - 1); // Go back to the previous step
  };

  return (
    <div className="form-container">
      <h2>Wedding Type Preferences</h2>
      <p>Please tell us about the type of wedding you envision.</p>

      <form>
        <div className="form-group">
          <label>
            <input
              type="radio"
              name="weddingType"
              value="local"
              checked={formData.weddingType === 'local'}
              onChange={handleWeddingTypeChange}
            />
            Local Wedding
          </label>
        </div>

        <div className="form-group">
          <label>
            <input
              type="radio"
              name="weddingType"
              value="national"
              checked={formData.weddingType === 'national'}
              onChange={handleWeddingTypeChange}
            />
            National Wedding
          </label>
        </div>

        <div className="form-group">
          <label>
            <input
              type="radio"
              name="weddingType"
              value="international"
              checked={formData.weddingType === 'international'}
              onChange={handleWeddingTypeChange}
            />
            International Destination Wedding
          </label>
        </div>

        {/* Show additional fields for Destination Wedding */}
        {formData.weddingType === 'international' && (
          <div className="form-group">
            <label htmlFor="destination">Preferred Destination:</label>
            <input
              type="text"
              id="destination"
              name="destination"
              value={formData.destination}
              onChange={(e) =>
                setFormData({ ...formData, destination: e.target.value })
              }
            />
          </div>
        )}

        <div className="form-navigation">
          <button type="button" onClick={handleBack} className="prev-btn">
            Previous
          </button>
          <button type="button" onClick={handleNext} className="next-btn">
            Next Step
          </button>
        </div>
      </form>
    </div>
  );
};

export default WeddingTypeForm;
