import React, { useState } from 'react';
import './Gallery.css';
import galleryData from '../data/galleryData';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Gallery = () => {
  const [selectedCategory, setSelectedCategory] = useState('venue');
  const [modalData, setModalData] = useState(null);

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  const handleImageClick = (item) => {
    setModalData(item);
  };

  const handleCloseModal = () => {
    setModalData(null);
  };

  const settings = {
    dots: false,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    arrows: true,
  };

  return (
    <div className="gallery-container">
      <div className="gallery-header">
        <h2>Our Work Gallery</h2>
        <p>Browse through the beautiful moments we've created for our clients.</p>
      </div>

      <div className="gallery-categories">
        {Object.keys(galleryData).map((category) => (
          <button
            key={category}
            className={`category-btn ${selectedCategory === category ? 'active' : ''}`}
            onClick={() => handleCategoryChange(category)}
          >
            {category.charAt(0).toUpperCase() + category.slice(1)}
          </button>
        ))}
      </div>

      <Slider {...settings} className="main-carousel">
        {galleryData[selectedCategory].map((item, index) => (
          <div key={index} className="gallery-item" onClick={() => handleImageClick(item)}>
            {item.type === 'image' ? (
              <img src={item.src} alt={item.text} className="gallery-img" />
            ) : (
              <div className="video-container">
                <video className="gallery-video" src={item.src} />
                <i className="play-icon fa fa-play-circle"></i>
              </div>
            )}
            <div className="overlay">{item.text}</div>
          </div>
        ))}
      </Slider>

      {modalData && (
        <div className="modal" onClick={handleCloseModal}>
          <div className="modal-content">
            {modalData.type === 'image' ? (
              <img src={modalData.src} alt={modalData.text} />
            ) : (
              <video controls src={modalData.src} />
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Gallery;
