import React, { useState } from 'react';
import './WeddingPlanningForm.css';

const WeddingPlanningForm = () => {
    const [step, setStep] = useState(1); // Track the current step of the form
    const [formData, setFormData] = useState({
        fullName: '',
        contactNumber: '',
        emailAddress: '',
        weddingDate: '',
        weddingLocation: '',
        eventType: '',
        brideDetails: {
            name: '',
            age: '',
            religion: '',
            culture: '',
            contactInfo: '',
        },
        groomDetails: {
            name: '',
            age: '',
            religion: '',
            culture: '',
            contactInfo: '',
        },
        weddingTheme: {
            themePreference: '',
            colorScheme: '',
            decorStyle: '',
        },
        venueDetails: {
            type: '',
            size: '',
            specialRequirements: '',
        },
        vendorPreferences: {
            photography: false,
            catering: false,
            entertainment: false,
        },
        specialRequestsForVendors: '',
        guestList: {
            expectedGuests: '',
            vipGuests: '',
            specialSeatingArrangements: '',
            accommodationRequirements: '',
        },
        budget: {
            venueBudget: '',
            decorBudget: '',
            cateringBudget: '',
            totalBudget: '',
        },
        additionalServices: {
            customServices: '',
            specialRequests: '',
        },
        consultationRequest: {
            preferredDateTime: '',
            additionalNotes: '',
        },
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleNext = () => {
        setStep(step + 1);
    };

    const handlePrev = () => {
        setStep(step - 1);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(formData);
    };

    return (
        <div className="wedding-planning-form">
            <h2>Wedding Planning Form</h2>
            <form onSubmit={handleSubmit}>
                {/* Step 1: Basic Details */}
                {step === 1 && (
                    <section className="form-section">
                        <h3>Basic Details</h3>
                        <input
                            type="text"
                            name="fullName"
                            value={formData.fullName}
                            onChange={handleChange}
                            placeholder="Full Name"
                        />
                        <input
                            type="text"
                            name="contactNumber"
                            value={formData.contactNumber}
                            onChange={handleChange}
                            placeholder="Contact Number"
                        />
                        <input
                            type="email"
                            name="emailAddress"
                            value={formData.emailAddress}
                            onChange={handleChange}
                            placeholder="Email Address"
                        />
                        <input
                            type="date"
                            name="weddingDate"
                            value={formData.weddingDate}
                            onChange={handleChange}
                        />
                        <input
                            type="text"
                            name="weddingLocation"
                            value={formData.weddingLocation}
                            onChange={handleChange}
                            placeholder="Wedding Location"
                        />
                        <select
                            name="eventType"
                            value={formData.eventType}
                            onChange={handleChange}
                        >
                            <option value="">Select Event Type</option>
                            <option value="Traditional">Traditional</option>
                            <option value="Modern">Modern</option>
                            <option value="Destination">Destination</option>
                        </select>
                    </section>
                )}

                {/* Step 2: Bride and Groom Details */}
                {step === 2 && (
                    <section className="form-section">
                        <h3>Bride and Groom Details</h3>
                        <div>
                            <h4>Bride Details</h4>
                            <input
                                type="text"
                                name="brideDetails.name"
                                value={formData.brideDetails.name}
                                onChange={handleChange}
                                placeholder="Bride's Name"
                            />
                            <input
                                type="number"
                                name="brideDetails.age"
                                value={formData.brideDetails.age}
                                onChange={handleChange}
                                placeholder="Bride's Age"
                            />
                            <input
                                type="text"
                                name="brideDetails.religion"
                                value={formData.brideDetails.religion}
                                onChange={handleChange}
                                placeholder="Bride's Religion"
                            />
                            <input
                                type="text"
                                name="brideDetails.culture"
                                value={formData.brideDetails.culture}
                                onChange={handleChange}
                                placeholder="Bride's Culture"
                            />
                            <input
                                type="text"
                                name="brideDetails.contactInfo"
                                value={formData.brideDetails.contactInfo}
                                onChange={handleChange}
                                placeholder="Bride's Contact Info"
                            />
                        </div>
                        <div>
                            <h4>Groom Details</h4>
                            <input
                                type="text"
                                name="groomDetails.name"
                                value={formData.groomDetails.name}
                                onChange={handleChange}
                                placeholder="Groom's Name"
                            />
                            <input
                                type="number"
                                name="groomDetails.age"
                                value={formData.groomDetails.age}
                                onChange={handleChange}
                                placeholder="Groom's Age"
                            />
                            <input
                                type="text"
                                name="groomDetails.religion"
                                value={formData.groomDetails.religion}
                                onChange={handleChange}
                                placeholder="Groom's Religion"
                            />
                            <input
                                type="text"
                                name="groomDetails.culture"
                                value={formData.groomDetails.culture}
                                onChange={handleChange}
                                placeholder="Groom's Culture"
                            />
                            <input
                                type="text"
                                name="groomDetails.contactInfo"
                                value={formData.groomDetails.contactInfo}
                                onChange={handleChange}
                                placeholder="Groom's Contact Info"
                            />
                        </div>
                    </section>
                )}

                {/* Step 3: Wedding Theme and Style */}
                {step === 3 && (
                    <section className="form-section">
                        <h3>Wedding Theme and Style</h3>
                        <input
                            type="text"
                            name="weddingTheme.themePreference"
                            value={formData.weddingTheme.themePreference}
                            onChange={handleChange}
                            placeholder="Theme Preference"
                        />
                        <input
                            type="text"
                            name="weddingTheme.colorScheme"
                            value={formData.weddingTheme.colorScheme}
                            onChange={handleChange}
                            placeholder="Color Scheme Preference"
                        />
                        <input
                            type="text"
                            name="weddingTheme.decorStyle"
                            value={formData.weddingTheme.decorStyle}
                            onChange={handleChange}
                            placeholder="Decor Style"
                        />
                    </section>
                )}

                {/* Step 4: Venue Preferences */}
                {step === 4 && (
                    <section className="form-section">
                        <h3>Venue Preferences</h3>
                        <input
                            type="text"
                            name="venueDetails.type"
                            value={formData.venueDetails.type}
                            onChange={handleChange}
                            placeholder="Type of Venue (Indoor, Outdoor, etc.)"
                        />
                        <input
                            type="text"
                            name="venueDetails.size"
                            value={formData.venueDetails.size}
                            onChange={handleChange}
                            placeholder="Venue Size"
                        />
                        <textarea
                            name="venueDetails.specialRequirements"
                            value={formData.venueDetails.specialRequirements}
                            onChange={handleChange}
                            placeholder="Special Venue Requirements"
                        />
                    </section>
                )}

                {/* Navigation Buttons */}
                <div className="form-navigation">
                    {step > 1 && (
                        <button type="button" onClick={handlePrev}>
                            Previous
                        </button>
                    )}
                    {step < 4 ? (
                        <button type="button" onClick={handleNext}>
                            Next
                        </button>
                    ) : (
                        <button type="submit">Submit</button>
                    )}
                </div>
            </form>
        </div>
    );
};

export default WeddingPlanningForm;
